mapper.insert:
INSERT INTO free_board (
bo_num , bo_title , bo_category
, bo_writer, bo_pass, bo_content
, bo_ip, bo_hit, bo_reg_date
, bo_del_yn
) VALUES (
seq_free_board.nextval , #{boTitle} , #{boCategory }
, #{boWriter} , #{boPass} , #{boContent}
, #{boIp} , 0 , sysdate
, 'N' )

mapper.update:
UPDATE free_board set
bo_title = #{boTitle}
,bo_category = #{boCategory }
,bo_content = #{boContent}
,bo_mod_date = sysdate
where bo_num = #{boNum}
and bo_pass = #{boPass}

mapper.delete:
UPDATE free_board
set
bo_del_yn = 'Y'
where bo_num = #{boNum}
and bo_pass = #{boPass}
