DROP TABLE MEMBER;
CREATE TABLE MEMBER (
   MEM_ID VARCHAR2(15) NOT NULL, 
   MEM_PASS VARCHAR2(15) NOT NULL, 
   MEM_NAME VARCHAR2(20)  NOT NULL, 
   MEM_REGNO1 CHAR(6), 
   MEM_REGNO2 CHAR(7), 
   MEM_BIR DATE, 
   MEM_ZIP CHAR(7), 
   MEM_ADD1 VARCHAR2(100), 
   MEM_ADD2 VARCHAR2(80), 
   MEM_HP VARCHAR2(15) NOT NULL, 
   MEM_MAIL VARCHAR2(40), 
   MEM_JOB VARCHAR2(40), 
   MEM_LIKE VARCHAR2(40), 
   MEM_MILEAGE NUMBER(10,0), 
   MEM_DEL_YN CHAR(1) DEFAULT 'N', 
   CONSTRAINTS PK_MEM_ID PRIMARY KEY (MEM_ID)  
 ); 

